import React, { Component } from 'react';
import Button from './Button';


class Timer extends Component {
    constructor(props){
      super(props)
      this.state = {
        time: 0,
        isOn: false,
        start: 0
      }

      this.startTimer = this.startTimer.bind(this)
      this.stopTimer = this.stopTimer.bind(this)
      this.resetTimer = this.resetTimer.bind(this)
    }

    startTimer() {
      this.setState({
        isOn: true,
        time: this.state.time,
        start: Date.now() - this.state.time
      })
      this.timer = setInterval(() => this.setState({
        time: Date.now() - this.state.start
      }), 1);
    }

    stopTimer() {
      this.setState({isOn: false})
      clearInterval(this.timer)
    }

    resetTimer() {
      this.setState({time: 0, isOn: false})
    }

    pad(n, width) {
      n = n + ''; 
      return n.length >= width ? n : new Array(width - n.length + 1).join('0') + n;
    }

    buttonTest() {
      alert('gg');
    }


    render() {

      let start = (this.state.time == 0) ?
        <button onClick={this.startTimer}>start</button> :
        null

      let stop = (this.state.time == 0 || !this.state.isOn) ?
        null :
        <button onClick={this.stopTimer}>stop</button>

      let resume = (this.state.time == 0 || this.state.isOn) ?
        null :
        <button onClick={this.startTimer}>resume</button>

      let reset = (this.state.time == 0 || this.state.isOn) ?
        null :
        <button onClick={this.resetTimer}>save</button>


      return(
        <div>
          <h1 className = 'timerDisplay'>{this.pad(Math.floor(this.state.time/60000), 2)}:{this.pad((Math.floor(this.state.time/1000) %60), 2)}

            :{this.pad((this.state.time%1000), 3)}</h1>
          {start}
          {resume}
          {stop}
          {reset}





          <Button handleClicked = {this.buttonTest}/>









        </div>
      )
    }
  }

  export default Timer
